     \usepackage{syntax}
   \usepackage{eulervm}
   \usepackage{mathpartir, amsmath, amsthm, amsfonts, amssymb}


      {\small 
        \begin {mathpar}

    \inferrule {
      \inferrule {
        \inferrule {
          I(p) = \top 
        }{
          I \models  p
        }
      }{
        I \nvDash  \neg  p
      }
      \quad 
      \inferrule {
        \inferrule {
          I(q) = \bot 
