html:pre.html:code:
inductive Node where | start : Node -- this will play the role of “a” | next : Node → Node
def succNode (v : Node) : Node := Node.next v
def natToNode : ℕ → Node | 0 => Node.start | n + 1 => succNode (natToNode n)
